mapper.select:
SELECT t.WORKDATE workdate, t.MSGID msgId, t.BATCHID batchId, t.FILENAME fileName, t.DTLBIZTIME dtlBizTime, t.MSGTYPE msgType, t.DTLMSGID dtlMsgId, t.INSTGDRCTPTY instgDrctPty, t.DBITPARTY dBITParty, t.CRDTPARTY cRDTParty, t.CCY ccy, t.AMOUNT amount, t.DTLBIZSTATUS dtlBizStatus, t.DTLDESC dtlDesc, t.PAYEENAME payeeName, t.PAYEEACCOUNT payeeAccount, t.PAYEEWALLETID payeeWalletId, t.PAYERACCOUNT payerAccount, t.OGNLMSGTYPE ognlMsgType, t.OGNLMSGID ognlMsgId, t.CHECKSTATUS checkStatus, t.LASTUPDATE lastUpDate, t.LASTUPTIME lastUpTime FROM PAY_BATCH_CHECKPATHDTL t where 1=1
AND t.WORKDATE = #{workdate}
AND t.BATCHID = #{batchId}
AND t.CHECKSTATUS !=#{checkstatus}
ORDER BY T.DTLBIZTIME

mapper.update:
UPDATE PAY_BATCH_CHECKPATHDTL
SET CHECKSTATUS = #{checkStatus}, LASTUPDATE = #{lastUpDate}, LASTUPTIME = #{lastUpTime}
WHERE MSGID = #{msgId}
AND DTLMSGID = #{dtlMsgId}

mapper.select:
    	select  count(1) from PAY_BATCH_CHECKPATHDTL a 
		inner join PAY_BATCH_CHECKCOLLECT b 
		on a.BATCHID = b.BATCHID and a.DTLMSGID = b.MSGID
		where a.WORKDATE = #{workdate}
		   and a.BATCHID = #{batchId}
		   and a.CHECKSTATUS = 'INIT'

mapper.update:
update PAY_BATCH_CHECKPATHDTL a inner join PAY_BATCH_CHECKCOLLECT b on a.BATCHID = b.BATCHID and a.DTLMSGID = b.MSGID set a.CHECKSTATUS = case when a.DTLBIZSTATUS in ('PR00', 'PR03') then /*互联互通交易成功*/ case when b.TRADESTATUS in ('1', 'A') then 'SAME' /*-我行成功*/ when b.TRADESTATUS in ('0', '3') then 'FANS' /*我行失败*/ else 'EANS' /*我行异常*/ end when a.DTLBIZSTATUS in ('PR01', 'PR04') then /*互联互通交易失败*/ case when b.TRADESTATUS in ('1', 'A') then 'SANF' /*我行成功*/ when b.TRADESTATUS in ('0', '3') then 'SAME' /*我行失败*/ else 'EANF' /*我行异常*/ end else 'PROC' /*互联互通交易处理中*/ end where a.WORKDATE = #{workdate} and a.BATCHID = #{batchId}
and a.CHECKSTATUS = 'INIT'
and a.DTLMSGID in
#{item}

mapper.select:
SELECT t.WORKDATE workdate, t.MSGID msgId, t.BATCHID batchId, t.FILENAME fileName, t.DTLBIZTIME dtlBizTime, t.MSGTYPE msgType, t.DTLMSGID dtlMsgId, t.INSTGDRCTPTY instgDrctPty, t.DBITPARTY dBITParty, t.CRDTPARTY cRDTParty, t.CCY ccy, t.AMOUNT amount, t.DTLBIZSTATUS dtlBizStatus, t.DTLDESC dtlDesc, t.PAYEENAME payeeName, t.PAYEEACCOUNT payeeAccount, t.PAYEEWALLETID payeeWalletId, t.PAYERACCOUNT payerAccount, t.OGNLMSGTYPE ognlMsgType, t.OGNLMSGID ognlMsgId, t.CHECKSTATUS checkStatus, t.LASTUPDATE lastUpDate, t.LASTUPTIME lastUpTime FROM PAY_BATCH_CHECKPATHDTL t left join PAY_BATCH_CHECKCOLLECT b on t.BATCHID = b.BATCHID and t.DTLMSGID = b.MSGID where t.WORKDATE = #{workdate} AND t.BATCHID = #{batchId} and b.MSGID is null
limit #{offset}, #{limit}
limit #{limit}
