INSERT INTO `config_info` VALUES (1, 'application-dev.yml', 'DEFAULT_GROUP', 'spring:\n  main:\n    allow-bean-definition-overriding: true\n  autoconfigure:\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\n\n#请求处理的超时时间\nribbon:\n  ReadTimeout: 10000\n  ConnectTimeout: 10000\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n', 'c07e6f7321493f6d5390d0a08bffb75a', '2019-11-29 16:31:20', '2020-12-21 15:29:24', NULL, '0:0:0:0:0:0:0:1', '', '', '通用配置', 'null', 'null', 'yaml', 'null');
INSERT INTO `config_info` VALUES (2, 'dcwlt-gateway-dev.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: localhost\n    port: 6379\n    password: \n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: dcwlt-auth\n          uri: lb://dcwlt-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 代码生成\n        - id: dcwlt-gen\n          uri: lb://dcwlt-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 定时任务\n        - id: dcwlt-job\n          uri: lb://dcwlt-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: dcwlt-system\n          uri: lb://dcwlt-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 支付批量服务模块\n        - id: dcwlt-pay-batch\n          uri: lb://dcwlt-pay-batch\n          predicates:\n            - Path=/pay-batch/**\n          filters:\n            - StripPrefix=1\n        # DCEP接入\n        - id: dcwlt-pay\n          uri: lb://dcwlt-pay-online\n          predicates:\n            - Path=/dcep/**\n          filters:\n            - XmlToJsonFilter=hugh\n            - StripPrefix=1\n            - JsonToXmlFilter    \n        # 文件服务\n        - id: dcwlt-file\n          uri: lb://dcwlt-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n\n# 不校验白名单\nignore:\n  whites:\n    - /auth/logout\n    - /auth/login\n    - /*/v2/api-docs\n    - /csrf\n    - /dcep\n', '3c596dd439308d30692113ff2358c57f', '2020-05-14 14:17:55', '2021-03-11 08:39:28', NULL, '10.7.25.56', '', '', '网关模块', 'null', 'null', 'yaml', 'null');
